$(document).ready(function () {
    // Navbar Dropdown Interaction
    $(".dropdown").on("show.bs.dropdown", function () {
        $(this).find(".dropdown-menu").first().slideDown(300); // Smooth dropdown show
    });

    $(".dropdown").on("hide.bs.dropdown", function () {
        $(this).find(".dropdown-menu").first().slideUp(200); // Smooth dropdown hide
    });

    // Smooth Scrolling for Links (For "About" section or internal navigation)
    $('a[href*="#"]:not([href="#"])').click(function () {
        if (location.pathname.replace(/^\//, '') === this.pathname.replace(/^\//, '') && location.hostname === this.hostname) {
            var target = $(this.hash);
            target = target.length ? target : $('[name=' + this.hash.slice(1) + ']');
            if (target.length) {
                $('html, body').animate({
                    scrollTop: target.offset().top
                }, 1000); // Adjust the scrolling speed
                return false;
            }
        }
    });

    // Example: Scroll Back to Top Button
    $(window).scroll(function () {
        if ($(this).scrollTop() > 100) {
            $('#back-to-top').fadeIn();
        } else {
            $('#back-to-top').fadeOut();
        }
    });

    // Scroll to top action
    $('#back-to-top').click(function () {
        $('html, body').animate({ scrollTop: 0 }, 800);
        return false;
    });
});
